import React from 'react'
import { useSelector } from 'react-redux'

function AppliedJobs() {
    // const appliedJobs =[1,2,3,4];
    const {allAppliedJobs} = useSelector(store =>store.job);
  return (
    <div className=''>
        {
            allAppliedJobs?.length > 0 ?(
                <table className="table table-bordered table-hover text-center">
                    <caption className='text-center my-2' style={{letterSpacing:"1px"}}>A list of your applied jobs</caption>
                    <thead>
                        <th>Date</th>
                        <th>Job Role</th>
                        <th>Company</th>
                        <th>Status</th>
                    </thead>
                    <tbody>
                        {
                            allAppliedJobs.map((appliedJob, index)=><tr key={index}>
                                <td>{appliedJob?.createdAt.split("T")[0]}</td>
                                <td>{appliedJob?.job ? (`${appliedJob?.job?.title}`):"NA"}</td>
                                <td>{appliedJob?.job ? (`${appliedJob?.job?.ti}`):"NA"}</td>
                                <td><span className='badge bg-dark'>{appliedJob?.status}</span></td>
                            </tr>)
                        }
                    </tbody>
                </table>
            ):<p className='text-center text-secondary' style={{letterSpacing:"1px"}}>No jobs applied</p>
        }
    </div>
  )
}

export default AppliedJobs
