import React, { useState } from 'react';
import Navbar from '../Ui/Navbar';
import { Link} from 'react-router-dom';
imp
import axios from 'axios';
import { toast } from 'sonner';

function Login() {
  const [input, setInput] = useState({
    email: "",
    password: "",
    role: "",
  });
  const navigate = useNavigate();

  const changeEventHandler = (e) => {
    setInput({ ...input, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("email", input.email);
    formData.append("password", input.password);
    formData.append("role", input.role);

    try {
      const res = await axios.post("http://localhost:8000/api/user/login", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });

      if (res.data.success) {
        navigate("/");
        toast.success(res.data.message);
      }
    } catch (error) {
      console.log(error);
      toast.error("An error occurred while submitting the form.");
    }
  };

  return (
    <div>
      <Navbar />
      <div className='form-container row my-3'>
        <form onSubmit={handleSubmit} className='form col-lg-5 col-md-6 col-sm-10 d-flex flex-column gap-3 mx-auto p-3 rounded-3 bg-light'>
          <h3>Login</h3>
          <p>Well back</p>
          <div className="input-field">
            <input
              required
              autoComplete="off"
              type="email"
              name="email"
              value={input.email}
              onChange={changeEventHandler}
            />
            <label htmlFor="email">Email</label>
          </div>
          <div className="input-field">
            <input
              required
              autoComplete="off"
              type="password"
              name="password"
              value={input.password}
              onChange={changeEventHandler}
            />
            <label htmlFor="password">Password</label>
          </div>
          <div className="d-flex gap-2">
            <input
              type="radio"
              id="student"
              name="role"
              value="student"
              checked={input.role === 'student'}
              onChange={changeEventHandler}
            />
            <label htmlFor="student">Student</label>

            <input
              type="radio"
              id="recruiter"
              name="role"
              value="recruiter"
              checked={input.role === 'recruiter'}
              onChange={changeEventHandler}
            />
            <label htmlFor="recruiter">Recruiter</label>
          </div>
          <div className="btn-container w-100 d-flex align-items-center gap-3">
            <button className="btn" type="submit">Login</button>
            <div className="acc-text">
              Don't have an account?
              <Link to="/Signup"><span style={{ color: "#0000ff", cursor: "pointer" }}>Signup</span></Link>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
}

export default Login;
