import React from 'react'
import { useState } from 'react'
function ProfileUpdateModal({isOpen, onClose}){
    if(isOpen) return null;
  return (
    <div className='modal-overlay d-flex justify-content-center align-items-center bg-dark te' onClick={onClose}>
        <div className='modal-content' onClick={(e)=> e.stopPropagation()}>
            <button className='modal-close' onClick={onClose}><i class="fa fa-close" aria-hidden="true"></i></button>
            <h6>Update Modal</h6>
            <p>Keeps the modal component generic and focused on layout and behavior, while the specific content is managed by the parent component.</p>
        </div>
    </div>
  )
}

export default ProfileUpdateModal
