import {configureStore} from "@reduxjs/toolkit";
import authSlice from "./authSlice";
import jobSlice from "./JobSlice";


import {
    persistStore,
    persistReducer,
    FLUSH,
    REHYDRATE,
    PAUSE,
    PERSIST,
    PURGE,
    REGISTER,
  } from 'redux-persist'
  import storage from 'redux-persist/lib/storage'


  

const store = configureStore({
    reducer:{
        auth:authSlice,
        job:jobSlice
    }
})
export default store;
