import React from 'react'
import { useState } from 'react'
import { Popover, Typography } from '@mui/meterial';
import santosh from '../Assets/santosh.jpg'

function Navbar() {
    const [anchorEl, setAnchorE1] = useState(null);
    const [isPopup, setIsPopup] = useState(false);
    const user = true;
    const handleClick = (event) => {
        setAnchorE1(event.currentTarget);
        setIsPopup(true);
    }
    const handleClose = () => {
        setAnchorE1(null);
        setIsPopup(false);
    }
    const id = isPopup ? 'simple-popover' : undefined;


    return (
        <div className='container'>
            <nav class="navbar navbar-expand-lg navbar-light bg-light">
                <div class="container-fluid">
                    <a class="navbar-brand" href="#">Navbar</a>
                    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
                        <span class="navbar-toggler-icon"></span>
                    </button>
                    <div class="collapse navbar-collapse" id="navbarNav">
                        <ul class="navbar-nav ms-auto d-flex gap-4 pe-5">
                            <li class="nav-item">
                                <a class="nav-link active" aria-current="page" href="#">Home</a>
                            </li>
                            <li class="nav-item">
                                <a class="nav-link active" href="#">Jobs</a>
                            </li>
                            <li class="nav-item">
                                <a class="nav-link active" href="#">Applications</a>
                            </li>
                        </ul>
                        {!user ? (
                            <div className='d-flex gap-4'>
                                <button className='btn btn-md bg-info'>Log In</button>
                                <button className='btn btn-md bg-secondary text-white'>Sign Up</button>
                            </div>
                        ) : (<div>
                            <button aria-describedby={id} variant="contained" onClick={handleClick}>
                                <img src={santosh} alt="Empty" className='rounded-circle' style={{ width: "40px", height: "40px" }} />
                            </button>
                            <Popover
                                id={id}
                                isPopup={isPopup}
                                anchorEl={anchorEl}
                                onClose={handleClose}
                                anchorOrigin={{
                                    vertical: 'bottom',
                                    horizontal: 'left',
                                }}
                            >
                                <Typography sx={{ p: 2 }}>The content of the Popover.</Typography>
                            </Popover>
                        </div>)

                        }

                    </div>
                </div>
            </nav>
        </div>
    )
}

export default Navbar
