import React, { useState } from 'react'
import Navbar from '../Ui/Navbar'
import { Link } from 'react-router-dom'
function Login() {
  const [input, setInput] = useState({
    
    email: "",
    password: "",
    role: "",
  });

  const changeEventHandler = (e) => {
    setInput({ ...input, [e.target.name]: e.target.value });
  };

  const changeFileHandler = (e) => {
    setInput({ ...input, file: e.target.files?.[0] });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(input);
  }


  return (
    <div>
      <Navbar />
      <div className='form-container row my-3 '>
        <form onSubmit className='form col-lg-5 col-md-6 col-sm-10 d-flex flex-column gap-3  mx-auto p-3 rounded-3 bg-light'>
          <h3>Login</h3>
          <p>Well back</p>

          <div className="input-field">
            <input
               required
               autoComplete="off"
               type="email"
               name="email"
               value={input.email}
               onChange={changeEventHandler}
            />
            <label htmlFor="email">Email</label>
          </div>
          <div className="input-field">
            <input
             required
             autoComplete="off"
             type="password"
             name="password"
             value={input.password}
             onChange={changeEventHandler}
            />
            <label htmlFor="username">Password</label>
          </div>
          <div className="d-flex gap-2">
            <input type="radio" id="recruiter" name="role" value="recruiter" />
            <label htmlFor="recruiter">Recruiter</label>

            <input type="radio" id="student" name="role" value="student" />
            <label htmlFor="student">Student</label>
          </div>

          <div className="btn-container w-100 d-flex align-items-center gap-3 ">
            <button className="btn">Login</button>
            <div className="acc-text">
              Don't have an account ?
              <Link to="/Signup"><span style={{ color: "#0000ff", cursor: "pointer" }}>Signup</span></Link>
            </div>
          </div>
        </form>
      </div>
    </div>
  )
}

export default Login
