import React from 'react'
import { Popover, Typography } from '@mui/material';
import Avathar1 from '../../Assets/Avathar.jpg'
import UpdateModal from './Updates/ProfileUpdateModal';
import { useState } from 'react';
import CompanyEditModal from './CompanyEditModal';
function CompaniesTable() {
    const [anchorEl, setAnchorEl] = useState(null);
    const [isPopup, setIsPopup] = useState(false);

    const handleClick = (event) => {
        setAnchorEl(event.currentTarget);
        setIsPopup(true);
    };

    const handleClose = () => {
        setAnchorEl(null);
        setIsPopup(false);
    };

    const id = isPopup ? 'simple-popover' : undefined;


    //Company details editing modal
    const [isModalOpen, setIsModalOpen] = useState(false);
    const handleOpenModal = () => { setIsModalOpen(true) };
    const handleCloseModal = () => { setIsModalOpen(false) };


    return (
        <div>
            <div>
                <table className='table table-bordered table-hover text-center'>
                    <thead>
                        <th>Logo</th>
                        <th>Name</th>
                        <th>Date</th>
                        <th>Action</th>
                    </thead>
                    <tbody>
                        <td>
                            <img src={Avathar1} style={{ width: "65px", borderRadius: "50%" }} />
                        </td>
                        <td>Company Name</td>
                        <td>01/08/2024</td>
                        <td>
                            <span className='bg-info' onClick={handleClick}><i class="fa fa-ellipsis-h " aria-hidden="true"></i></span>
                            <Popover
                                id={id}
                                open={isPopup}
                                anchorEl={anchorEl}
                                onClose={handleClose}
                                anchorOrigin={{
                                    vertical: 'bottom',
                                    horizontal: 'left',
                                }}
                            >
                                <Typography sx={{ p: 2 }} className=''>
                                    <div>
                                        <span>
                                            <i className="fa-solid fa-pen-to-square fs-5 p-2" onClick={handleOpenModal}></i>
                                            <CompanyUpdateModal isOpen={isModalOpen} onClose={handleCloseModal} />
                                        </span>
                                    </div>
                                </Typography>
                            </Popover>
                        </td>
                    </tbody>
                </table>
            </div>
        </div>
    )
}

export default CompaniesTable
