import React from 'react';
import Modal from 'react-modal'; // or your preferred modal library

const ProfileUpdateModal = ({ isOpen, onClose }) => {
    return (
        <Modal 
        isOpen={isOpen} 
        onRequestClose={onClose}
        className='h-100 col-lg-5 col-md-5 col-sm-7 p-2 m-4  '
        style={{border:"hidden"}}
        >
            <div className='' style={{backgroundColor:"#fff",boxShadow:"5px 5px 8px gray"}}>
                <div className='d-flex justify-content-between'>
                    <h2 className='modal-header'>Update Profile</h2>
                    <i class="fa fa-close fs-4" aria-hidden="true" onClick={onClose}></i>
                    {/* Modal content goes here */}
                </div>
                <div>
                    <p>
                        Land your dream job with captivating CVs you can professionally customize to reflect your true potential with Canva's free resume templates and easy-to-use design editor.
                    </p>
                </div>
            </div>
        </Modal>
    );
};

export default ProfileUpdateModal;
