import React, { useEffect, useState } from 'react';
import { Popover, Typography } from '@mui/material';
import CompanyUpdateModal from '../Ui/Updates/CompanyUpdateModal';
import { useSelector } from 'react-redux';

function CompaniesTable() {
    const [anchorEl, setAnchorEl] = useState(null);
    const [selectedCompany, setSelectedCompany] = useState(null);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const { companies ,searchCompanyByText} = useSelector(store => store.company);
    const [filterCompany, setFilterCompany] = useState(companies);

    const handleClick = (event, company) => {
        setAnchorEl(event.currentTarget);
        setSelectedCompany(company);
    };

    const handleClosePopover = () => {
        setAnchorEl(null);
        setSelectedCompany(null);
    };

    const handleOpenModal = () => {
        setIsModalOpen(true);
        handleClosePopover();
    };

    const handleCloseModal = () => {
        setIsModalOpen(false);
    };

    const open = Boolean(anchorEl);
    const id = open ? 'simple-popover' : undefined;
    useEffect(()=>{

    },[companies,])
    return (
        <div className='container'>
            <div>
                <table className='table bg-success table-bordered table-hover text-center'>
                    <thead className='bg-info'>
                        <tr>
                            <th className='py-2'>Logo</th>
                            <th className='py-2'>Name</th>
                            <th className='py-2'>Date</th>
                            <th className='py-2'>Action</th>
                        </tr>
                    </thead>
                    {
                        companies.length <= 0 ? (
                            <tbody>
                                <tr>
                                    <td colSpan="4">You haven't registered any company yet.</td>
                                </tr>
                            </tbody>
                        ) : (
                            <tbody>
                                {
                                    companies.map((company) => (
                                        <tr key={company?._id} className='bg-info'>
                                            <td>
                                                <img src={company.logo} alt='No logo found' style={{ width: "65px", borderRadius: "50%" }} />
                                            </td>
                                            <td className='pt-4'>{company.name}</td>
                                            <td className='pt-4'>{company.createdAt.split("T")[0]}</td>
                                            <td className='pt-3'>
                                                <i className="fa fa-ellipsis-h fs-4 p-2" onClick={(e) => handleClick(e, company)} aria-hidden="true"></i>
                                                <Popover
                                                    id={id}
                                                    open={open}
                                                    anchorEl={anchorEl}
                                                    onClose={handleClosePopover}
                                                    anchorOrigin={{
                                                        vertical: 'bottom',
                                                        horizontal: 'left',
                                                    }}
                                                >
                                                    <Typography sx={{ p: 2 }} className=''>
                                                        <div>
                                                            <i className="fa-solid fa-pen-to-square fs-5 p-2" onClick={handleOpenModal}></i>
                                                        </div>
                                                    </Typography>
                                                </Popover>
                                            </td>
                                        </tr>
                                    )
                                    )
                                }
                            </tbody>
                        )
                    }
                </table>
            </div>
            <CompanyUpdateModal isOpen={isModalOpen} onClose={handleCloseModal} company={selectedCompany} />
        </div>
    );
}

export default CompaniesTable;
