import React from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
// import './CategoryCarousel.css'; // External CSS file (if needed)

const CategoryCarousel = () => {
  const categories = [
    "Frontend Developer", "Backend Developer", "Data Science", "Graphic Designer",
    "FullStack Developer", "DevOps Developer", "AI Developer", "Mobile Development",
    "Software Engineer", "Gaming Developer", "Cloud Developer"
  ];

  const settings = {
    infinite: true,
    speed: 500,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 2000,
    arrows: true,
    responsive: [
      {
        breakpoint: 992, // lg breakpoint
        settings: {
          slidesToShow: 3,
        }
      },
      {
        breakpoint: 768, // md breakpoint
        settings: {
          slidesToShow: 2,
        }
      },
      {
        breakpoint: 576, // sm breakpoint
        settings: {
          slidesToShow: 1,
        }
      }
    ]
  };

  return (
    <div className="carousel-container">
      <Slider {...settings}>
        {categories.map((item, index) => (
          <div key={index} className="carousel-item">
            <button>{item}</button>
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default CategoryCarousel;
