import React from 'react'
import { Popover, Typography } from '@mui/material';
import Avathar1 from '../../Assets/Avathar.jpg'
import CompanyUpdateModal from '../Ui/Updates/CompanyUpdateModal';
import { useState } from 'react';
import { useSelector } from 'react-redux'
function CompaniesTable() {
    const [anchorEl, setAnchorEl] = useState(null);
    const [isPopup, setIsPopup] = useState(false);
    const { companies } = useSelector(store => store.compay);
    const handleClick = (event) => {
        setAnchorEl(event.currentTarget);
        setIsPopup(true);
    };

    const handleClose = () => {
        setAnchorEl(null);
        setIsPopup(false);
    };

    const id = isPopup ? 'simple-popover' : undefined;


    //Company details editing modal
    const [isModalOpen, setIsModalOpen] = useState(false);
    const handleOpenModal = () => { setIsModalOpen(true) };
    const handleCloseModal = () => { setIsModalOpen(false) };


    return (
        <div className='container'>
            <div>
                <table className='table table-bordered table-hover text-center'>
                    <thead>
                        <th>Logo</th>
                        <th>Name</th>
                        <th>Date</th>
                        <th>Action</th>
                    </thead>
                    <tbody>
                        {
                            companies.length < 0 ? <span>You havan't registered any company yet.</span> : (
                                <div>
                                    {
                                        companies?.map((company) => {
                                            return (
                                                <tr >
                                                    <td>
                                                        <img src={Avathar1} style={{ width: "65px", borderRadius: "50%" }} />
                                                    </td>
                                                    <td className='pt-4'>Company Name</td>
                                                    <td className='pt-4'>01/08/2024</td>
                                                    <td className='pt-3'>
                                                        <i className="fa fa-ellipsis-h fs-4 p-2" onClick={handleClick} aria-hidden="true"></i>
                                                        <Popover
                                                            id={id}
                                                            open={isPopup}
                                                            anchorEl={anchorEl}
                                                            onClose={handleClose}
                                                            anchorOrigin={{
                                                                vertical: 'bottom',
                                                                horizontal: 'left',
                                                            }}
                                                        >
                                                            <Typography sx={{ p: 2 }} className=''>
                                                                <div>
                                                                    <i className="fa-solid fa-pen-to-square fs-5 p-2" onClick={handleOpenModal}></i>
                                                                    <CompanyUpdateModal isOpen={isModalOpen} onClose={handleCloseModal} />
                                                                </div>
                                                            </Typography>
                                                        </Popover>
                                                    </td>
                                                </tr>
                                            )
                                        })
                                    }
                                </div>
                            )
                        }

                    </tbody>
                </table>
            </div>
        </div>
    )
}

export default CompaniesTable
