import React from 'react';
import Modal from 'react-modal'; // Ensure you have installed react-modal

// Set the app element for accessibility (adjust the ID as needed)
Modal.setAppElement('#root'); 

const ProfileUpdateModal = ({ isOpen, onClose }) => {
    // Custom styles for the modal
    const customStyles = {
        content: {
            position: 'absolute',
            top: '50%',
            left: '50%',
            right: 'auto',
            bottom: 'auto',
            transform: 'translate(-50%, -50%)',
            width: '90%',
            maxWidth: '480px',
            padding: '20px',
            borderRadius: '8px',
            backgroundColor: '#fff',
            boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
            border: 'none',
        },
        overlay: {
            backgroundColor: 'rgba(0, 0, 0, 0.5)', // Semi-transparent overlay
        },
    };

    return (
        <Modal
            isOpen={isOpen}
            onRequestClose={onClose}
            style={customStyles}
            contentLabel="Update Profile Modal"
        >
            <div className='modal-header d-flex align-items-center justify-content-between border-bottom border-1 border-secondary pb-1'>
                <h3>Update Profile</h3>
                <button 
                    className='btn btn-close  ' 
                    style={{background:"none", border:"none", cursor:"pointer"}}
                    onClick={onClose} 
                    aria-label="Close"
                >
                    <i className="fa fa-close fs-4" style={{color:"#333"}} aria-hidden="true"></i>
                </button>
            </div>
            <form className='modal-body py-3 d-flex flex-column gap-2 text-dark fw-bold'>
                <div className='d-flex justify-content-between'>
                    <label>Full Name :</label>
                    <input type='text' name='fullname' />
                </div>
                <div className='d-flex justify-content-between'>
                    <label>Email :</label>
                    <input type='email' id='email' nam/>
                </div>
                <div className='d-flex justify-content-between'>
                    <label htmlFor='number'>Phone Number :</label>
                    <input type='text' id='number' name='number'/>
                </div>
                <div className='d-flex justify-content-between'>
                    <label htmlFor='bio'>Bio :</label>
                    <input type='text' id='bio' name='bio' />
                </div>
                <div className='d-flex justify-content-between'>
                    <label htmlFor='skills'>Skills :</label>
                    <input type="text" id='skills' name='skills'/>
                </div>
                <div className='d-flex justify-content-between'>
                    <label htmlFor='file'>Resume :</label>
                    <input type='file' id='file' name='file' accept='application/pdf'/>
                </div>
                <button className='w-100 m-2 p-1 rounded bg-dark text-light'>Update Profile</button>
            </form>
        </Modal>
    );
};

export default ProfileUpdateModal;
