import React, { useEffect, useState } from 'react'
import { RadioGroup, Radio } from '@mui/material'

const filterData = [
    {
        filterType: "Location",
        array: ["Delhi NCR", "Bangaloor", "Hyderabad", "Pune", "Chennai", "Mumbai"]
    },
    {
        filterType: "Industry",
        array: ["Frontend Developer", "Backend Developer", "Data Science", "Graphic Designer",
            "FullStack Developer", "DevOps Developer", "AI Developer", "Mobile Development",
            "Software Engineer", "Gaming Developer", "Cloud Developer"]
    }
]

function FilterCard() {

    const [selectedFilterValue, setSelectedFilterValue] = useState();

    const handleChange = (value) =>{
        seelectedFilterValue(value);
    }
    useEffect (()=>{
        console.log(selectedFilterValue);
        
    },[selectedFilterValue])
    
    return (
        <div style={{ backgroundColor: "#fff" }}>
            <div>
                <h6>Filter Jobs</h6>
                <hr className='mt-3' />
            </div>
            <RadioGroup name="radio-buttons-group" value={selectedFilterValue} onValueChange={handleChange}>
                {
                    filterData.map((data, index) => (
                        <div className='text-start'>
                            <h6>{data.filterType}</h6>
                            {
                                data.array.map((item, itemIndex) => {
                                    const itemId = `id${index}-${itemIndex}`
                                    return (
                                        <div className='d-flex gap-2 text-sm' style={{ fontSize: '15px' }}>
                                            <Radio value={item} id={item} />
                                            <label htmlFor = {itemId}>{item}</label>
                                        </div>
                                    )
                                })
                            }
                        </div>
                    ))
                }
            </RadioGroup>

        </div>
    )
}

export default FilterCard
