import React from 'react'
import LatestJobCards from './LatestJobCards';
import {useSelector} from 'react-redux';
function LatestJobs() {
    const randomJobs = [1, 2, 3, 4, 5, 6, 7, 8]
    const {allJobs} = useSelector(store =>store.jobs);
    return (
        <div>
            <div className='p-4' style={{backgroundColor:"#F5F5F5"}}>
                <h1><span className='' style={{ color: "#d32f2f" }}>Latest & Top</span> <span style={{ color: "#112164" }}>Job Openings</span></h1>
                <div className='jobcards my-5'>
                    {
                        allJobs.length < 0 ? <span>No job available</span> : allJobs?.slice(0,6).map((item) => <LatestJobCards key={item._id} />)
                    }
                </div>
            </div>

        </div>
    )
}

export default LatestJobs
