import React, { useEffect } from 'react'
import { Button } from 'react-bootstrap';
import { useParams } from 'react-router-dom';
// import getSignleJob from '../Hooks/GetSingleJob';
import { setSingleJob } from '../../redux/JobSlice';
import axios from 'axios';
import { useDispatch } from 'react-redux';
import { JOB_API_ENDPOINT } from '../Endpoints/constant';

function JobDescription() {
    const isJobApplied = true;
    const params = useParams();
    const jobId = params.id;
    
   const dispatch = useDispatch();


    useEffect(() => {
        const fetchSingleJob = async () => {
            try {
                const res = await axios.get(`${JOB_API_ENDPOINT}/get/${jobId}`, { withCredentials: true })
                if (res.data.success) {
                    dispatch(setSingleJob(res.data.jobs));
                }
            } catch (error) {
                console.log(error);
            }
        }
        fetchSingleJob();
    }, [jobId, dispatch, user?._id]);

    return (
        <div className='p-4' style={{ backgroundColor: "#F5F5F5", height: '100VH' }}>
            <div className='m-4 p-3' style={{ backgroundColor: "#fff" }}>
                <div className='d-flex justify-content-between'>
                    <div>
                        <h5 className='my-2'>Frontend developer</h5>
                        <div className='d-flex gap-2'>
                            <div className='badge border' style={{ color: "#482121", backgroundColor: "#FFF5E4" }}>12 positions</div>
                            <div className='badge border' style={{ color: "#D1512D", backgroundColor: "#FFF5E4" }}>Full Time</div>
                            <div className='badge border' style={{ color: "#213363", backgroundColor: "#FFF5E4" }}>8LPA</div>
                        </div>
                    </div>
                    <dvi>
                        <Button className='btn text-light' style={{ backgroundColor: isJobApplied ? '#7A1CAC' : '#CD5C08' }}>{isJobApplied ? "Allready Applied" : "Applay Now"}</Button>
                    </dvi>
                </div>
                <div>
                    <h6 className='botder-2 border-bottom border-secondary my-3 pb-2'>Job description</h6>
                    <div>
                        {/* <h6 className=''>Role :<span>Frontend Developer</span></h6> */}
                        <h6 className='' >Role : <span className='fw-normal text-secondary'>Frontend Developer</span></h6>
                        <h6 >Location : <span className='fw-normal text-secondary'>Hyderabad</span></h6>
                        <h6>Description : <span className='fw-normal text-secondary'>Lorem ipsum, dolor sit amet consectetur adipsisicing elit. Praesentium similiwue sed dolor!</span></h6>
                        <h6>Experience : <span className='fw-normal text-secondary'>2 years</span></h6>
                        <h6>Salary : <span className='fw-normal text-secondary'>12 LPA</span></h6>
                        <h6>Total Applicants : <span className='fw-normal text-secondary'>4</span></h6>
                        <h6>Posted Date : <span className='fw-normal text-secondary'>15-08-2024</span></h6>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default JobDescription
