import React from 'react';
import { useState } from 'react';
import { Popover, Typography } from '@mui/material';
import { useSelector } from 'react-redux';


const shortlistingStatus = ["Accepted", "Rejected"];

function ApplicantsTable() {
    const { applicants } = useSelector(store => store.application);
    const [anchorEl, setAnchorEl] = useState(null);

    const handleClick = (event) => {
        setAnchorEl(event.currentTarget);
    };

    const handleClosePopover = () => {
        setAnchorEl(null);
    };

    const open = Boolean(anchorEl);
    const id = open ? 'simple-popover' : undefined;

    return (
        <div>
            <table className='table bg-success table-bordered table-hover text-center'>
                <caption className='text-center my-2'>{applicants?.applications.length <=0 ? "No one applied to this job":"A list of all Applicants."}</caption>
                <thead>
                    <tr>
                        <th>Full Name</th>
                        <th>Email</th>
                        <th>Contact</th>
                        <th>Resume</th>
                        <th>Date</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        applicants && applicants?.applications.map((item) => (
                            <tr>
                                <td>{item?.applicant?.fullname}</td>
                                <td>{item?.applicant?.email}</td>
                                <td>{item?.applicant?.phoneNumber}</td>
                                <td><a href={item?.applicant?.profile?.resume} t>{item?.applicant?.profile?.resumeOriginalName}</a></td>
                                <td>{item?.applicant?.createdAt.split("T")[0]}</td>
                                <td>
                                    <i className="fa fa-ellipsis-h fs-4 p-2" style={{ cursor: "pointer" }} onClick={handleClick} aria-hidden="true"></i>
                                    <Popover
                                        id={id}
                                        open={open}
                                        anchorEl={anchorEl}
                                        onClose={handleClosePopover}
                                        anchorOrigin={{
                                            vertical: 'bottom',
                                            horizontal: 'left',
                                        }}
                                    >
                                        <Typography sx={{ padding: '10px' }}>
                                            <div className='px-2 py-2' style={{ cursor: "pointer" }}>
                                                {shortlistingStatus.map((status, index) => (
                                                    <div key={index} >
                                                        {status}
                                                    </div>
                                                ))}
                                            </div>
                                        </Typography>
                                    </Popover>
                                </td>
                            </tr>
                        ))
                    }

                </tbody>
            </table>
        </div>
    );
}

export default ApplicantsTable;
