import React from 'react';
import { useSpring, animated } from '@react-spring/web';

function CategoryCarousel() {
  const categories = [
    "Frontend Developer", "Backend Developer", "Data Science", "Graphic Designer",
    "FullStack Developer", "DevOps Developer", "AI Developer", "Mobile Development",
    "Software Engineer", "Gaming Developer", "Cloud Developer"
  ];

  const [index, setIndex] = React.useState(0);
  const totalCategories = categories.length;

  const props = useSpring({
    transform: `translateX(-${index * 100}%)`,
    config: { duration: 500 },
  });

  const handlePrev = () => setIndex((prev) => (prev === 0 ? totalCategories - 1 : prev - 1));
  const handleNext = () => setIndex((prev) => (prev === totalCategories - 1 ? 0 : prev + 1));

  return (
    <div className='carousel-container'>
      <button className='carousel-button prev' onClick={handlePrev}>←</button>
      <animated.div className='carousel-wrapper' style={props}>
        {categories.map((category, idx) => (
          <div key={idx} className='carousel-item'>
            <h3>{category}</h3>
          </div>
        ))}
      </animated.div>
      <button className='carousel-button next' onClick={handleNext}>→</button>
    </div>
  );
}

export default CategoryCarousel;
