import React from 'react';
import Modal from 'react-modal'; // Ensure you have installed react-modal

// Set the app element for accessibility (adjust the ID as needed)
Modal.setAppElement('#root'); 

const ProfileUpdateModal = ({ isOpen, onClose }) => {
    // Custom styles for the modal
    const customStyles = {
        content: {
            position: 'absolute',
            top: '50%',
            left: '50%',
            right: 'auto',
            bottom: 'auto',
            transform: 'translate(-50%, -50%)',
            width: '90%', // Responsive width
            maxWidth: '600px', // Maximum width to avoid it being too large
            padding: '20px',
            borderRadius: '8px',
            backgroundColor: '#fff',
            boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
            border: 'none', // Remove the border
        },
        overlay: {
            backgroundColor: 'rgba(0, 0, 0, 0.5)', // Semi-transparent overlay
        },
    };

    return (
        <Modal
            isOpen={isOpen}
            onRequestClose={onClose}
            style={customStyles}
            contentLabel="Update Profile Modal"
        >
            <div className='modal-header d-flex align-items-center justify-content-between border-bottom border-1 border-secondary pb-1'>
                <h3>Update Profile</h3>
                <button 
                    className='btn btn-close  ' 
                    style={{background:"none", border:"none", cursor:"pointer"}}
                    onClick={onClose} 
                    aria-label="Close"
                >
                    <i className="fa fa-close fs-4" style={{color:"#333"}} aria-hidden="true"></i>
                </button>
            </div>
            <div className='modal-body pt-2'>
                <div>
                    <label></label>
                </div>
            </div>
        </Modal>
    );
};

export default ProfileUpdateModal;
