import React from 'react'

const shortlistingStatus = ["Accepted", "Rejected"];
function ApplicantsTable() {
    const [anchorEl, setAnchorEl] = useState(null);

    const handleClick = (event, job) => {
        setAnchorEl(event.currentTarget);

    };

    const handleClosePopover = () => {
        setAnchorEl(null);
    };
    return (
        <div>
            <table className='table bg-success table-bordered table-hover text-center'>
                <caption className='text-center my-2'>A list of all Applicants.</caption>
                <thead>
                    <tr>
                        <th>Full Name</th>
                        <th>Email</th>
                        <th>Contact</th>
                        <th>Resume</th>
                        <th>Date</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td>Full Name</td>
                        <td>Email</td>
                        <td>Contact</td>
                        <td>Resume</td>
                        <td>Date</td>
                        <td>

                            <i className="fa fa-ellipsis-h fs-4 p-2" onClick={(e) => handleClick(e, job)} aria-hidden="true"></i>
                            <Popover
                                id={id}
                                open={open}
                                anchorEl={anchorEl}
                                onClose={handleClosePopover}
                                anchorOrigin={{
                                    vertical: 'bottom',
                                    horizontal: 'left',
                                }}
                            >
                                <Typography sx={{}}>
                                    <div className='px-2 py-2'>
                                        {
                                            shortlistingStatus.map((status, index) => {
                                                return (
                                                    <div key={index}>

                                                    </div>
                                                )
                                            })
                                        }
                                    </div>
                                </Typography>
                            </Popover>

                        </td>
                    </tr>
                </tbody>
            </table>
        </div>
    )
}

export default ApplicantsTable
