// CategoryCarousel.js
import React from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
// import './CategoryCarousel.css'; // External CSS file

const CategoryCarousel = () => {
  const categories = [
    "Frontend Developer", "Backend Developer", "Data Science", "Graphic Designer",
    "FullStack Developer", "DevOps Developer", "AI Developer", "Mobile Development",
    "Software Engineer", "Gaming Developer", "Cloud Developer"
  ];

  const settings = {
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 2000,
    arrows: true,
  };

  return (
    <div className="carousel-container m">
      <Slider {...settings}>
        {categories.map((item, index) => (
          <div key={index} className="carousel-item">
            <button>{item}</button>
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default CategoryCarousel;
