import React from 'react'
import Avathar1 from '../../Assets/Avathar.jpg'
function CompaniesTable() {
    const [anchorEl, setAnchorEl] = useState(null);
    const [isPopup, setIsPopup] = useState(false);

    const handleClick = (event) => {
        setAnchorEl(event.currentTarget);
        setIsPopup(true);
    };

    const handleClose = () => {
        setAnchorEl(null);
        setIsPopup(false);
    };

    const id = isPopup ? 'simple-popover' : undefined;
    return (
        <div>
            <div>
                <table className='table table-bordered table-hover text-center'>
                    <thead>
                        <th>Logo</th>
                        <th>Name</th>
                        <th>Date</th>
                        <th>Action</th>
                    </thead>
                    <tbody>
                        <td>
                            <img src={Avathar1} style={{ width: "65px", borderRadius: "50%" }} />
                        </td>
                        <td>Company Name</td>
                        <td>01/08/2024</td>
                        <td>
                            <span ><i class="fa fa-ellipsis-h " aria-hidden="true"></i></span>
                            <Popover
                                id={id}
                                open={isPopup}
                                anchorEl={anchorEl}
                                onClose={handleClose}
                                anchorOrigin={{
                                    vertical: 'bottom',
                                    horizontal: 'left',
                                }}
                            >
                                <Typography sx={{ p: 2 }} className=''>
                                    <div className=''>
                                        <div className='d-flex gap-4'>
                                            <img
                                                src={user?.profile?.profilePhoto}
                                                alt="User"
                                                className='rounded-circle'
                                                style={{ width: "45px", height: "45px" }}
                                            />
                                            <div>
                                                <h5 className='fw-bold' style={{ letterSpacing: "1px" }}>{user?.fullname}</h5>
                                                <div className='text-secondary' style={{ fontSize: "15px" }} >{user?.profile?.bio}</div>
                                            </div>
                                        </div>
                                        {
                                            user && user.role === "student" && (
                                                <div className='my-3'>
                                                    <i className="fa fa-user me-4" aria-hidden="true"></i>
                                                    <Link to="/Profile">View Profile</Link>
                                                </div>
                                            )
                                        }

                                        <div className='mt-4'>
                                            <i className="fa fa-sign-out me-4" aria-hidden="true"></i>
                                            <button className='btn btn-sm text-light BlueButton' onClick={handleLogout}>Log Out</button>
                                        </div>
                                    </div>
                                </Typography>
                            </Popover>
                        </td>
                    </tbody>
                </table>
            </div>
        </div>
    )
}

export default CompaniesTable
