import React, { useState } from 'react';
import { Popover, Typography } from '@mui/material';
import santosh from '../Assets/santosh.jpg';

function Navbar() {
    const [anchorEl, setAnchorEl] = useState(null);
    const [isPopup, setIsPopup] = useState(false);
    const [isOpen, setIsOpen] = useState(false);
    const user = true;

    const handleClick = (event) => {
        setAnchorEl(event.currentTarget);
        setIsPopup(true);
    };

    const handleClose = () => {
        setAnchorEl(null);
        setIsPopup(false);
    };

    const id = isPopup ? 'simple-popover' : undefined;

    return (
        <div className='container'>
            <nav className="navbar navbar-expand-lg navbar-light bg-light">
                <div className="container-fluid">
                    <a className="navbar-brand " href="#">Job Portal</a>
                    <div className="navbar-toggler" style={{border:'none'}} data-bs-toggle="collapse" data-bs-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
                        <input id="checkbox2" type="checkbox" />
                            <label className="toggle toggle2" for="checkbox2">
                                <div id="bar4" className="bars"></div>
                                <div id="bar5" className="bars"></div>
                                <div id="bar6" className="bars"></div>
                            </label>
                    </div>
                    <div className="collapse navbar-collapse" id="navbarNav">
                        <ul className="navbar-nav ms-auto d-flex gap-4 pe-5">
                            <li className="nav-item">
                                <a className="nav-link active" aria-current="page" href="#">Home</a>
                            </li>
                            <li className="nav-item">
                                <a className="nav-link active" href="#">Jobs</a>
                            </li>
                            <li className="nav-item">
                                <a className="nav-link active" href="#">Applications</a>
                            </li>
                        </ul>
                        {!user ? (
                            <div className='d-flex gap-4'>
                                <button className='btn btn-md bg-info'>Log In</button>
                                <button className='btn btn-md bg-secondary text-white'>Sign Up</button>
                            </div>
                        ) : (
                            <div>
                                <img
                                    src={santosh}
                                    alt="User"
                                    className='rounded-circle'
                                    style={{ width: "40px", height: "40px" }}
                                    aria-describedby={id}
                                    onClick={handleClick}
                                />
                                <Popover
                                    id={id}
                                    open={isPopup}
                                    anchorEl={anchorEl}
                                    onClose={handleClose}
                                    anchorOrigin={{
                                        vertical: 'bottom',
                                        horizontal: 'left',
                                    }}
                                >
                                    <Typography sx={{ p: 2 }}>
                                        <div>
                                            <h6>Well come</h6>
                                            M
                                        </div>
                                    </Typography>
                                </Popover>
                            </div>
                        )}
                    </div>
                </div>
            </nav>
        </div>
    );
}

export default Navbar;
