import React, { useState } from 'react';
import Navbar from '../Ui/Navbar';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'sonner';

function Signup() {
    const [conformPassword, setConformPassword] = useState("");
    const [input, setInput] = useState({
        fullname: "",
        email: "",
        phoneNumber: "",
        password: "",
        role: "",
        file: ""
    });
    const navigate = useNavigate();

    const changeEventHandler = (e) => {
        setInput({ ...input, [e.target.name]: e.target.value });
    };

    const changeFileHandler = (e) => {
        setInput({ ...input, file: e.target.files?.[0] });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (input.password !== conformPassword) {
            toast.error("Passwords do not match.");
            return;
        }

        const formData = new FormData();
        formData.append("fullname", input.fullname);
        formData.append("email", input.email);
        formData.append("phoneNumber", input.phoneNumber);
        formData.append("password", input.password);
        formData.append("role", input.role);
        if (input.file) {
            formData.append("file", input.file);
        }

        try {
            const res = await axios.post("http://localhost:8000/api/user/register", formData, {
                headers: {
                    "Content-Type": "multipart/form-data"
                }
            });

            if (res.data.success) {
                navigate("/Login");
                toast.success(res.data.message);
            } else {
                toast.error(res.data.message || "An error occurred");
            }
        } catch (error) {
            console.log(error);
            toast.error("An error occurred while submitting the form.");
        }
    };

    return (
        <div>
            <Navbar />
            <div className='form-container row my-3'>
                <form onSubmit={handleSubmit} className='form col-lg-5 col-md-6 col-sm-10 d-flex flex-column gap-3 mx-auto p-3 rounded-3'>
                    <h3>Signup</h3>
                    <p>Signup now and get full access to our app.</p>
                    <div className="input-field">
                        <input
                            required
                            autoComplete="off"
                            type="text"
                            value={input.fullname}
                            name="fullname"
                            id='fullname'
                            onChange={changeEventHandler}
                        />
                        <label htmlFor="fullname">Full Name</label>
                    </div>
                    <div className="input-field">
                        <input
                            required
                            autoComplete="off"
                            type="text"
                            name="phoneNumber"
                            value={input.phoneNumber}
                            id="phoneNumber"
                            onChange={changeEventHandler}
                        />
                        <label htmlFor="phoneNumber">Phone Number</label>
                    </div>
                    <div className="input-field">
                        <input
                            required
                            autoComplete="off"
                            type="email"
                            name="email"
                            value={input.email}
                            id="email"
                            onChange={changeEventHandler}
                        />
                        <label htmlFor="email">Email</label>
                    </div>
                    <div className="input-field">
                        <input
                            required
                            autoComplete="off"
                            type="password"
                            name="password"
                            value={input.password}
                            id="password"
                            onChange={changeEventHandler}
                        />
                        <label htmlFor="password">Password</label>
                    </div>
                    <div className="input-field">
                        <input
                            required
                            autoComplete="off"
                            type="password"
                            name="conformPassword"
                            value={conformPassword}
                            id="conformPassword"
                            onChange={(e) => { setConformPassword(e.target.value); }}
                        />
                        <label htmlFor="conformPassword">Confirm Password</label>
                    </div>
                    <div className="d-flex gap-2">
                        <input
                            type="radio"
                            id="recruiter"
                            name="role"
                            value="recruiter"
                            checked={input.role === 'recruiter'}
                            onChange={changeEventHandler}
                        />
                        <label htmlFor="recruiter">Recruiter</label>

                        <input
                            type="radio"
                            id="student"
                            name="role"
                            value="student"
                            checked={input.role === 'student'}
                            onChange={changeEventHandler}
                        />
                        <label htmlFor="student">Student</label>
                    </div>
                    <div className="d-flex flex-column gap-2">
                        <label htmlFor="file">Profile Picture</label>
                        <input
                            type='file'
                            id="file"
                            accept='image/*'
                            className='cursor-pointer'
                            onChange={changeFileHandler}
                        />
                    </div>
                    <div className="btn-container w-100 d-flex align-items-center gap-3">
                        <button className="btn" type="submit">Signup</button>
                        <div className="acc-text">
                            Already have an account?
                            <Link to="/Login"><span style={{ color: "#0000ff", cursor: "pointer" }}>Login</span></Link>
                        </div>
                    </div>
                </form>
            </div>
        </div>
    );
}

export default Signup;
