import React from 'react'
import { Popover, Typography } from '@mui/material';
import Avathar1 from '../../Assets/Avathar.jpg'
import CompanyUpdateModal from '../Ui/Updates/CompanyUpdateModal';
import { useState } from 'react';
import { useSelector } from 'react-redux'
function CompaniesTable() {
    const [anchorEl, setAnchorEl] = useState(null);
    const [isPopup, setIsPopup] = useState(false);
    const { companies} = useSelector(store => store.company);
    const handleClick = (event) => {
        setAnchorEl(event.currentTarget);
        setIsPopup(true);
    };

    const handleClose = () => {
        setAnchorEl(null);
        setIsPopup(false);
    };

    const id = isPopup ? 'simple-popover' : undefined;


    //Company details editing modal
    const [isModalOpen, setIsModalOpen] = useState(false);
    const handleOpenModal = () => { setIsModalOpen(true) };
    const handleCloseModal = () => { setIsModalOpen(false) };


    return (
        <div className='container'>
            <div>
                <table className='table bg-success table-bordered table-hover text-center'>
                    <thead className='bg-info'>
                        <th className='py-2'>Logo</th>
                        <th className='py-2'>Name</th>
                        <th className='py-2'>Date</th>
                        <th className='py-2'>Action</th>
                    </thead>
                        {
                            companies.length <= 0 ? <span>You havan't registered any company yet.</span> : (
                                <tbody>
                                    {
                                        companies?.map((company) => 
                                            (
                                                <tr key={company?._id} className='bg-info'>
                                                    <td>
                                                        <img src={company.logo} alt='No log found' style={{ width: "65px", borderRadius: "50%" }} />
                                                    </td>
                                                    <td className='pt-4'>{company.name}</td>
                                                    <td className='pt-4'>{company.createdAt}</td>
                                                    <td className='pt-3'>
                                                        <i className="fa fa-ellipsis-h fs-4 p-2" onClick={handleClick} aria-hidden="true"></i>
                                                        <Popover
                                                            id={id}
                                                            open={isPopup}
                                                            anchorEl={anchorEl}
                                                            onClose={handleClose}
                                                            anchorOrigin={{
                                                                vertical: 'bottom',
                                                                horizontal: 'left',
                                                            }}
                                                        >
                                                            <Typography sx={{ p: 2 }} className=''>
                                                                <div>
                                                                    <i className="fa-solid fa-pen-to-square fs-5 p-2" onClick={handleOpenModal}></i>
                                                                    <CompanyUpdateModal isOpen={isModalOpen} onClose={handleCloseModal} />
                                                                </div>
                                                            </Typography>
                                                        </Popover>
                                                    </td>
                                                </tr>
                                            )
                                        )
                                    }
                                </tbody>
                            )
                        }
                </table>
            </div>
        </div>
    )
}

export default CompaniesTable
