import React from 'react';
import Navbar from '../Ui/Navbar';
import { Link, useNavigate } from 'react-router-dom';
import { useState } from 'react';
import axios from 'axios'
import {USER_API_ENDPOINT} from '../Endpoints/constant.js'
import { toast } from 'sonner';

function Signup() {
    const [confromPassword, setConfromPassword] = useState("");
    const [input, setInput] = useState({
        fullname: "",
        email: "",
        phoneNumber: "",
        password: "",
        role: "",
        file: ""
    });
    const navigate = useNavigate();

    const changeEventHandler = (e) => {
        setInput({ ...input, [e.target.name]: e.target.value });
    };

    const changeFileHandler = (e) => {
        setInput({ ...input, file: e.target.files?.[0] });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(input);
        const formData = new formData();
        formData.append("fullname", input.fullname);
        formData.append("email", input.email);
        formData.append("phoneNumber", input.phoneNumber);
        formData.append("password", input.password);
        formData.append("role", input.role);
        if (input.profile)
            formData.append("fullname", input.fullname);
        try {
            const res = axios.post(`${USER_API_ENDPOINT}/register`, formData,{
                headers:{
                    "Content-Type":"multipart/form-data"
                }
            });
            if(res.data.success){
                navigate("/Login");
                toast.success(res.data.message)
            }
        } catch (error) {
            console.log(error)

        }
    }

    return (
        <div>
            <Navbar />
            <div className='form-container row my-3'>
                <form onSubmit={handleSubmit} className='form col-lg-5 col-md-6 col-sm-10 d-flex flex-column gap-3 mx-auto p-3 rounded-3'>
                    <h3>Signup</h3>
                    <p>Signup now and get full access to our app.</p>
                    <div className="input-field">
                        <input
                            required
                            autoComplete="off"
                            type="text"
                            value={input.fullname}
                            name="fullname"
                            id='fullname'
                            onChange={changeEventHandler}
                        />
                        <label htmlFor="fullname">Full Name</label>
                    </div>
                    <div className="input-field">
                        <input
                            required
                            autoComplete="off"
                            type="text"
                            name="phoneNumber"
                            value={input.phoneNumber}
                            onChange={changeEventHandler}
                        />
                        <label htmlFor="phonenumber">Phone Number</label>
                    </div>
                    <div className="input-field">
                        <input
                            required
                            autoComplete="off"
                            type="email"
                            name="email"
                            value={input.email}
                            onChange={changeEventHandler}
                        />
                        <label htmlFor="email">Email</label>
                    </div>
                    <div className="input-field">
                        <input
                            required
                            autoComplete="off"
                            type="password"
                            name="password"
                            value={input.password}
                            onChange={changeEventHandler}
                        />
                        <label htmlFor="password">Password</label>
                    </div>
                    <div className="input-field">
                        <input
                            required
                            autoComplete="off"
                            type="password"
                            name="conformPassword"
                            value={confromPassword}
                            id="conformPassword"
                            onChange={(e) => { setConfromPassword(e.target.value) }}
                        />
                        <label htmlFor="conformPassword">Confirm Password</label>
                    </div>
                    <div className="d-flex gap-2">
                        <input
                            type="radio"
                            id="recruiter" // Fixed id here
                            name="role"
                            value="recruiter"
                            checked={input.role === 'recruiter'}
                            onChange={changeEventHandler} // Updated to use the correct handler
                        />
                        <label htmlFor="recruiter">Recruiter</label>

                        <input
                            type="radio"
                            id="student"
                            name="role"
                            value="student"
                            checked={input.role === 'student'}
                            onChange={changeEventHandler}
                        />
                        <label htmlFor="student">Student</label>
                        <label>Profile</label>
                        <input
                            type='file'
                            accept='image/*'
                            className='cursor-pointer'
                            onChange={changeFileHandler}
                        />
                    </div>
                    <div className="btn-container w-100 d-flex align-items-center gap-3">
                        <button className="btn">Signup</button>
                        <div className="acc-text">
                            Already have an account?
                            <Link to="/Login"><span style={{ color: "#0000ff", cursor: "pointer" }}>Login</span></Link>
                        </div>
                    </div>
                </form>
            </div>
        </div>
    );
}

export default Signup;
