// src/Components/Ui/CategoryCarousel.js
import React from 'react';
import Slider from 'react-slick';
import 'slick-carousel/slick/slick.css';
import 'slick-carousel/slick/slick-theme.css';
import useWindowSize from './useWindowSize'; // Ensure this path is correct

const CategoryCarousel = () => {
  const { width } = useWindowSize(); // Get the current screen width

  const settings = {
    infinite: true,
    speed: 500,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 2000,
    arrows: true,
    slidesToShow: getSlidesToShow(width), // Set slidesToShow dynamically
  };

  const getSlidesToShow = (width) => {
    if (width >= 992) return 3; // lg screens
    if (width >= 768) return 2; // md screens
    return 1; // sm screens
  };

  const categories = [
    "Frontend Developer", "Backend Developer", "Data Science", "Graphic Designer",
    "FullStack Developer", "DevOps Developer", "AI Developer", "Mobile Development",
    "Software Engineer", "Gaming Developer", "Cloud Developer"
  ];

  return (
    <div className="carousel-container">
      <Slider {...settings}>
        {categories.map((item, index) => (
          <div key={index} className="carousel-item">
            <button>{item}</button>
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default CategoryCarousel;
